// Color tokens for dark & classic themes
export const themes = {
  dark: {
    bg: "bg-neutral-950",
    surface: "bg-neutral-900/60",
    card: "bg-neutral-900/80",
    text: "text-neutral-100",
    mutetext: "text-neutral-400",
    ring: "ring-1 ring-neutral-800",
    grad: "from-indigo-500/20 via-fuchsia-500/10 to-cyan-500/10",
    accent: "bg-gradient-to-r from-indigo-500 via-fuchsia-500 to-cyan-500",
  },
  classic: {
    bg: "bg-stone-50",
    surface: "bg-white/80",
    card: "bg-white",
    text: "text-stone-900",
    mutetext: "text-stone-600",
    ring: "ring-1 ring-stone-200",
    grad: "from-stone-300/50 via-amber-200/50 to-stone-100/50",
    accent: "bg-gradient-to-r from-stone-900 via-stone-700 to-stone-500",
  },
};
